import React, { useState } from "react";

const DoubleSlit = ({
  wavelength,
  distance,
  slitSeparation,
  showParticles,
}) => {
  const slitWidth = 10


  const visualScalingFactor = .1
  const personalSeperation = slitSeparation * visualScalingFactor
  const personalDistance = distance * visualScalingFactor
  console.log(wavelength, distance, slitSeparation, showParticles)
  console.log(personalDistance)

  const drawCanvas = (ctx) => {
    // Clear canvas
    ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);
    const canvasWidth = ctx.canvas.width;
    const canvasHeight = ctx.canvas.height;

    // Draw slits
    ctx.fillStyle = "black";
    ctx.fillRect(
      canvasWidth / 2 - personalSeperation / 2,
      canvasHeight - 10,
      slitWidth,
      personalDistance
    );

    ctx.fillRect(
      canvasWidth / 2 + personalSeperation / 2,
      canvasHeight - 10,
      slitWidth,
      slitWidth
    );

    // Draw wave pattern
    ctx.strokeStyle = "blue";
    ctx.beginPath();
    for (let x = 0; x < canvasWidth; x++) {
      const y =
        canvasHeight / 2 +
        Math.sin(((x - canvasWidth / 2) * 2 * Math.PI) / wavelength) * 50;
      if (x === 0) {
        ctx.moveTo(x, y);
      } else {
        ctx.lineTo(x, y);
      }
    }
    ctx.stroke();
  };
  return (
    <div>
      <canvas
        width={500}
        height={300}
        ref={(canvas) => canvas && drawCanvas(canvas.getContext("2d"))}
      />
    </div>
  );
};

export default DoubleSlit;
