import React, { useState } from "react";

const DoubleSlit = ({
  wavelength,
  distance,
  slitSeparation,
  showParticles,
}) => {
  const slitWidth = 20


  const visualScalingFactor = .1
  const personalSeperation = slitSeparation * visualScalingFactor
  const personalDistance = distance * visualScalingFactor
  console.log(wavelength, distance, slitSeparation, showParticles)
  console.log(personalDistance)



  const drawCanvas = (ctx) => {

    // Clear canvas
    ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);
    const canvasWidth = ctx.canvas.width;
    const canvasHeight = ctx.canvas.height;
    const drawLine = (info, style = {}) => {
      const { x, y, x1, y1 } = info;
      const { color = "black", width = 1 } = style;

      ctx.beginPath();
      ctx.moveTo(x, y);
      ctx.lineTo(x1, y1);
      ctx.strokeStyle = color;
      ctx.lineWidth = width;
      ctx.stroke();
    };

    // Draw slits
    ctx.fillStyle = "green";
    // ctx.fillRect(
    //   canvasWidth / 2 - personalSeperation / 2,
    //   canvasHeight - 10,
    //   slitWidth,
    //   personalDistance
    // );

    // ctx.fillRect(
    //   canvasWidth / 2 + personalSeperation / 2,
    //   canvasHeight - 10,
    //   slitWidth,
    //   slitWidth
    // );


    // left slit
    drawLine({
      x: canvasWidth / 2 - personalSeperation / 2-slitWidth/2,
      y: canvasHeight - 10,
      x1: canvasWidth / 2 - personalSeperation / 2-slitWidth/2,
      y1: canvasHeight+personalDistance-10,
    });
    drawLine({
      x: canvasWidth / 2 - personalSeperation / 2+slitWidth/2, 
      y: canvasHeight - 10,
      x1: canvasWidth / 2 - personalSeperation / 2 + slitWidth/2,
      y1: canvasHeight + personalDistance - 10,
    });


    // right slit
    drawLine({
      x: canvasWidth / 2 + personalSeperation / 2-slitWidth/2,
      y: canvasHeight - 10,
      x1: canvasWidth / 2 + personalSeperation / 2-slitWidth/2,
      y1: canvasHeight + personalDistance - 10,
    });
    drawLine({
      x: canvasWidth / 2 + personalSeperation / 2+slitWidth/2,
      y: canvasHeight - 10,
      x1: canvasWidth / 2 + personalSeperation / 2+slitWidth/2,
      y1: canvasHeight + personalDistance - 10,
    });

    const midHeight = canvasHeight - 10 +5;

    // connecting bar
    drawLine({
      x: canvasWidth / 2 - personalSeperation / 2 + slitWidth/2,
      x1: canvasWidth / 2 + personalSeperation / 2-slitWidth/2,
      y: midHeight,
      y1: midHeight,
    });

    // outer bars
    drawLine({
      x: 0,
      x1: canvasWidth / 2 - personalSeperation / 2 - slitWidth / 2,
      y: midHeight,
      y1: midHeight,
    });
    drawLine({
      x: canvasWidth / 2 + personalSeperation / 2 + slitWidth / 2,
      x1: canvasWidth,
      y: midHeight,
      y1: midHeight,
    });
    // then we draw a horizontal line between the two slits



    // Draw wave pattern

  };
  return (
    <div>
      <canvas
        width={500}
        height={distance/30}
        ref={(canvas) => canvas && drawCanvas(canvas.getContext("2d"))}
      />
    </div>
  );
};

export default DoubleSlit;
